// src/VideoEditor.tsx
import React, { useState, useRef, useEffect } from "react";

type Id = string;

type VideoAsset = {
  id: Id;
  name: string;
  url: string;
  duration?: number; // en segundos, opcional
};

type AudioAsset = {
  id: Id;
  name: string;
  url: string;
  duration?: number;
};

type VideoSegment = {
  id: Id;
  assetId: Id;
  start: number; // segundos
  end: number;   // segundos
};

type AudioSegment = {
  id: Id;
  assetId: Id;
  start: number;
  end: number;
};

function createId(): Id {
  return Math.random().toString(36).slice(2) + Date.now().toString(36);
}

const VideoEditor: React.FC = () => {
  // Assets
  const [videoAssets, setVideoAssets] = useState<VideoAsset[]>([]);
  const [audioAssets, setAudioAssets] = useState<AudioAsset[]>([]);

  // Timeline
  const [videoSegments, setVideoSegments] = useState<VideoSegment[]>([]);
  const [audioSegments, setAudioSegments] = useState<AudioSegment[]>([]);

  // Selección
  const [selectedVideoSegmentId, setSelectedVideoSegmentId] = useState<Id | null>(null);
  const [selectedAudioSegmentId, setSelectedAudioSegmentId] = useState<Id | null>(null);

  // Reproductores
  const videoRef = useRef<HTMLVideoElement | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  // --- Smooth seek: evita el salto brusco al inicio del vídeo ---
  const smoothSeek = (video: HTMLVideoElement, time: number) => {
    return new Promise<void>((resolve) => {
      const handle = () => {
        video.removeEventListener("seeked", handle);
        resolve();
      };
      video.addEventListener("seeked", handle);
      video.currentTime = time;
    });
  };


  // --- NUEVO: reproducción secuencial de la timeline ---
  const [isTimelinePlaying, setIsTimelinePlaying] = useState(false);
  const [currentTimelineIndex, setCurrentTimelineIndex] = useState<number | null>(null);


  const selectedVideoSegment = videoSegments.find(s => s.id === selectedVideoSegmentId) || null;
  const selectedVideoAsset = selectedVideoSegment
    ? videoAssets.find(a => a.id === selectedVideoSegment.assetId) || null
    : null;

  const selectedAudioSegment = audioSegments.find(s => s.id === selectedAudioSegmentId) || null;
  const selectedAudioAsset = selectedAudioSegment
    ? audioAssets.find(a => a.id === selectedAudioSegment.assetId) || null
    : null;

    // --- NUEVO: cuando estamos reproduciendo la timeline y cambia de segmento,
  // colocamos el vídeo al inicio de ese segmento y le damos play.
  useEffect(() => {
    if (!isTimelinePlaying) return;
    const video = videoRef.current;
    if (!video || !selectedVideoSegment) return;

    (async () => {
      await smoothSeek(video, selectedVideoSegment.start);
      video.play();
    })();

  }, [isTimelinePlaying, selectedVideoSegment]);


  // Manejar subida de vídeos
  const handleVideoUpload: React.ChangeEventHandler<HTMLInputElement> = (e) => {
    const files = e.target.files;
    if (!files) return;

    const newAssets: VideoAsset[] = [];
    Array.from(files).forEach((file) => {
      const url = URL.createObjectURL(file);
      newAssets.push({
        id: createId(),
        name: file.name,
        url,
      });
    });

    setVideoAssets(prev => [...prev, ...newAssets]);
    e.target.value = "";
  };

  // Manejar subida de audios
  const handleAudioUpload: React.ChangeEventHandler<HTMLInputElement> = (e) => {
    const files = e.target.files;
    if (!files) return;

    const newAssets: AudioAsset[] = [];
    Array.from(files).forEach((file) => {
      const url = URL.createObjectURL(file);
      newAssets.push({
        id: createId(),
        name: file.name,
        url,
      });
    });

    setAudioAssets(prev => [...prev, ...newAssets]);
    e.target.value = "";
  };

  // Añadir un vídeo a la timeline como segmento entero (0 → duración aprox 10s si no se conoce)
  const addVideoToTimeline = (asset: VideoAsset) => {
    const defaultDuration = asset.duration ?? 10;
    const segment: VideoSegment = {
      id: createId(),
      assetId: asset.id,
      start: 0,
      end: defaultDuration,
    };
    setVideoSegments(prev => [...prev, segment]);
    setSelectedVideoSegmentId(segment.id);
  };

  // Añadir audio a la timeline (igual idea)
  const addAudioToTimeline = (asset: AudioAsset) => {
    const defaultDuration = asset.duration ?? 10;
    const segment: AudioSegment = {
      id: createId(),
      assetId: asset.id,
      start: 0,
      end: defaultDuration,
    };
    setAudioSegments(prev => [...prev, segment]);
    setSelectedAudioSegmentId(segment.id);
  };

  // Actualizar metadatos duration de vídeos cuando se cargan
  const handleVideoLoadedMetadata = (assetId: Id, duration: number) => {
    setVideoAssets(prev =>
      prev.map(a => a.id === assetId ? { ...a, duration } : a)
    );
  };

  const handleAudioLoadedMetadata = (assetId: Id, duration: number) => {
    setAudioAssets(prev =>
      prev.map(a => a.id === assetId ? { ...a, duration } : a)
    );
  };

  // Reproducción limitada al segmento seleccionado
  const handlePlay = () => {
    if (!videoRef.current || !selectedVideoSegment || !selectedVideoAsset) return;

    const video = videoRef.current;
    (async () => {
      await smoothSeek(video, selectedVideoSegment.start);
      video.play();
    })();

  };

  // Pausar
  const handlePause = () => {
    if (videoRef.current) videoRef.current.pause();
  };

  // --- NUEVO: reproducir / pausar la timeline completa ---
  const handleToggleTimelinePlay = () => {
    const video = videoRef.current;
    if (!video) return;
    if (videoSegments.length === 0) return;

    // Si ya estamos reproduciendo la timeline → pausa
    if (isTimelinePlaying) {
      video.pause();
      setIsTimelinePlaying(false);
      return;
    }

    // Elegir desde qué segmento empezamos:
    // - si hay uno seleccionado, empezamos desde ese
    // - si no, desde el primero
    const startIndexFromSelected = selectedVideoSegment
      ? videoSegments.findIndex(s => s.id === selectedVideoSegment.id)
      : 0;

    const startIndex =
      startIndexFromSelected >= 0 ? startIndexFromSelected : 0;

    const firstSeg = videoSegments[startIndex];

    setCurrentTimelineIndex(startIndex);
    setSelectedVideoSegmentId(firstSeg.id); // el efecto de arriba colocará el vídeo y hará play
    setIsTimelinePlaying(true);
  };


  // Parar al llegar al final del segmento
  // y, si estamos en modo "timeline", saltar al siguiente segmento
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const onTimeUpdate = () => {
      if (!selectedVideoSegment) return;

      if (video.currentTime >= selectedVideoSegment.end) {
        // Si NO estamos reproduciendo la timeline, se comporta como antes
        if (!isTimelinePlaying) {
          video.pause();
          return;
        }

        // Si estamos en modo timeline, pasar al siguiente segmento
        setCurrentTimelineIndex((prev) => {
          if (prev == null) return prev;
          const nextIndex = prev + 1;

          // Si no hay más segmentos → parar
          if (nextIndex >= videoSegments.length) {
            video.pause();
            setIsTimelinePlaying(false);
            return null;
          }

          const nextSeg = videoSegments[nextIndex];
          setSelectedVideoSegmentId(nextSeg.id); // esto hará que el otro useEffect lo coloque y haga play
          return nextIndex;
        });
      }
    };

    video.addEventListener("timeupdate", onTimeUpdate);
    return () => {
      video.removeEventListener("timeupdate", onTimeUpdate);
    };
  }, [selectedVideoSegment, isTimelinePlaying, videoSegments]);


  // Cambiar valores de un segmento de vídeo
  const updateVideoSegment = (id: Id, patch: Partial<VideoSegment>) => {
    setVideoSegments(prev =>
      prev.map(s => s.id === id ? { ...s, ...patch } : s)
    );
  };

  const deleteVideoSegment = (id: Id) => {
    setVideoSegments(prev => prev.filter(s => s.id !== id));
    if (selectedVideoSegmentId === id) setSelectedVideoSegmentId(null);
  };

  // Cambiar valores de un segmento de audio
  const updateAudioSegment = (id: Id, patch: Partial<AudioSegment>) => {
    setAudioSegments(prev =>
      prev.map(s => s.id === id ? { ...s, ...patch } : s)
    );
  };

  const deleteAudioSegment = (id: Id) => {
    setAudioSegments(prev => prev.filter(s => s.id !== id));
    if (selectedAudioSegmentId === id) setSelectedAudioSegmentId(null);
  };

  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "system-ui, sans-serif" }}>
      {/* Sidebar izquierda: assets */}
      <aside style={{ width: "260px", borderRight: "1px solid #ddd", padding: "12px", boxSizing: "border-box" }}>
        <h2 style={{ fontSize: "1.1rem", marginBottom: "8px" }}>Assets</h2>

        <div style={{ marginBottom: "16px" }}>
          <h3 style={{ fontSize: "0.95rem" }}>Vídeos</h3>
          <input type="file" accept="video/*" multiple onChange={handleVideoUpload} />
          <ul style={{ listStyle: "none", padding: 0, marginTop: "8px", maxHeight: "150px", overflowY: "auto" }}>
            {videoAssets.map(asset => (
              <li key={asset.id} style={{ marginBottom: "6px" }}>
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", gap: "4px" }}>
                  <span style={{ fontSize: "0.85rem", overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap" }}>
                    {asset.name}
                  </span>
                  <button
                    style={{ fontSize: "0.75rem" }}
                    onClick={() => addVideoToTimeline(asset)}
                  >
                    + Timeline
                  </button>
                </div>

                {/* Video oculto solo para capturar duration */}
                {!asset.duration && (
                  <video
                    src={asset.url}
                    style={{ display: "none" }}
                    onLoadedMetadata={(e) =>
                      handleVideoLoadedMetadata(asset.id, e.currentTarget.duration)
                    }
                  />
                )}
              </li>
            ))}
          </ul>
        </div>

        <div>
          <h3 style={{ fontSize: "0.95rem" }}>Audios</h3>
          <input type="file" accept="audio/*" multiple onChange={handleAudioUpload} />
          <ul style={{ listStyle: "none", padding: 0, marginTop: "8px", maxHeight: "150px", overflowY: "auto" }}>
            {audioAssets.map(asset => (
              <li key={asset.id} style={{ marginBottom: "6px" }}>
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", gap: "4px" }}>
                  <span style={{ fontSize: "0.85rem", overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap" }}>
                    {asset.name}
                  </span>
                  <button
                    style={{ fontSize: "0.75rem" }}
                    onClick={() => addAudioToTimeline(asset)}
                  >
                    + Timeline
                  </button>
                </div>

                {!asset.duration && (
                  <audio
                    src={asset.url}
                    style={{ display: "none" }}
                    onLoadedMetadata={(e) =>
                      handleAudioLoadedMetadata(asset.id, e.currentTarget.duration)
                    }
                  />
                )}
              </li>
            ))}
          </ul>
        </div>
      </aside>

      {/* Zona central: preview + timeline */}
      <main style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        {/* Barra superior / info */}
        <header style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", display: "flex", justifyContent: "space-between" }}>
          <div>
            <strong>Proyecto sin nombre</strong>
          </div>
          <button disabled style={{ opacity: 0.6 }}>Exportar (mock)</button>
        </header>

        {/* Preview + controles */}
        <section style={{ padding: "12px", display: "flex", gap: "16px", borderBottom: "1px solid #eee" }}>
          <div style={{ flex: 2 }}>
            <h3 style={{ fontSize: "0.95rem", marginBottom: "8px" }}>Preview</h3>
            <div style={{ background: "#000", borderRadius: "4px", overflow: "hidden", maxWidth: "100%", height: "320px", display: "flex", alignItems: "center", justifyContent: "center" }}>
              {selectedVideoAsset ? (
                <video
                  ref={videoRef}
                  src={selectedVideoAsset.url}
                  style={{ maxWidth: "100%", maxHeight: "100%" }}
                  controls={false}
                />
              ) : (
                <span style={{ color: "#aaa", fontSize: "0.9rem" }}>
                  Selecciona un segmento de vídeo en la timeline
                </span>
              )}
            </div>
            <div style={{ marginTop: "8px", display: "flex", gap: "8px" }}>
              <button
                onClick={handleToggleTimelinePlay}
                disabled={videoSegments.length === 0}
              >
                {isTimelinePlaying ? "⏸ Pausar timeline" : "▶ Play timeline"}
              </button>
            </div>


          </div>

          {/* Preview de audio seleccionado */}
          <div style={{ flex: 1 }}>
            <h3 style={{ fontSize: "0.95rem", marginBottom: "8px" }}>Audio seleccionado</h3>
            {selectedAudioAsset ? (
              <div>
                <p style={{ fontSize: "0.85rem" }}>{selectedAudioAsset.name}</p>
                <audio
                  ref={audioRef}
                  src={selectedAudioAsset.url}
                  controls
                  style={{ width: "100%" }}
                />
              </div>
            ) : (
              <span style={{ color: "#aaa", fontSize: "0.9rem" }}>
                Selecciona un segmento de audio en la timeline
              </span>
            )}
          </div>
        </section>

        {/* Timeline */}
        <section style={{ flex: 1, display: "flex", flexDirection: "column", padding: "12px", gap: "8px" }}>
          <h3 style={{ fontSize: "0.95rem" }}>Timeline</h3>

          {/* Pista de vídeo */}
          <div style={{ marginBottom: "12px" }}>
            <div style={{ fontSize: "0.85rem", marginBottom: "4px" }}>Vídeo</div>
            <div style={{ border: "1px solid #ccc", borderRadius: "4px", padding: "6px", minHeight: "60px", display: "flex", gap: "8px", overflowX: "auto" }}>
              {videoSegments.length === 0 && (
                <span style={{ fontSize: "0.8rem", color: "#888" }}>
                  Arrastra vídeos a la timeline utilizando el botón “+ Timeline”.
                </span>
              )}
              {videoSegments.map((seg) => {
                const asset = videoAssets.find(a => a.id === seg.assetId);
                const isSelected = seg.id === selectedVideoSegmentId;
                return (
                  <div
                    key={seg.id}
                    onClick={() => setSelectedVideoSegmentId(seg.id)}
                    style={{
                      minWidth: "160px",
                      padding: "4px",
                      borderRadius: "4px",
                      border: isSelected ? "2px solid #2563eb" : "1px solid #ccc",
                      background: isSelected ? "#eff6ff" : "#f9fafb",
                      cursor: "pointer",
                      fontSize: "0.8rem",
                    }}
                  >
                    <div style={{ fontWeight: 600, marginBottom: "2px" }}>
                      {asset?.name ?? "Video"}
                    </div>
                    <div>start: {seg.start.toFixed(2)}s</div>
                    <div>end: {seg.end.toFixed(2)}s</div>
                    <button
                      style={{ marginTop: "4px", fontSize: "0.7rem" }}
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteVideoSegment(seg.id);
                      }}
                    >
                      Eliminar
                    </button>
                  </div>
                );
              })}
            </div>

            {/* Controles del segmento de vídeo seleccionado */}
            {selectedVideoSegment && (
              <div style={{ marginTop: "8px", padding: "8px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "0.8rem" }}>
                <div style={{ marginBottom: "4px", fontWeight: 600 }}>Editar segmento de vídeo</div>
                <div style={{ display: "flex", gap: "8px", alignItems: "center", marginBottom: "4px" }}>
                  <label>
                    start (s):{" "}
                    <input
                      type="number"
                      step="0.1"
                      value={selectedVideoSegment.start}
                      onChange={(e) =>
                        updateVideoSegment(selectedVideoSegment.id, { start: Number(e.target.value) || 0 })
                      }
                      style={{ width: "80px" }}
                    />
                  </label>
                  <label>
                    end (s):{" "}
                    <input
                      type="number"
                      step="0.1"
                      value={selectedVideoSegment.end}
                      onChange={(e) =>
                        updateVideoSegment(selectedVideoSegment.id, { end: Number(e.target.value) || 0 })
                      }
                      style={{ width: "80px" }}
                    />
                  </label>
                </div>
              </div>
            )}
          </div>

          {/* Pista de audio */}
          <div>
            <div style={{ fontSize: "0.85rem", marginBottom: "4px" }}>Audio</div>
            <div style={{ border: "1px solid #ccc", borderRadius: "4px", padding: "6px", minHeight: "60px", display: "flex", gap: "8px", overflowX: "auto" }}>
              {audioSegments.length === 0 && (
                <span style={{ fontSize: "0.8rem", color: "#888" }}>
                  Añade audios a la timeline con el botón “+ Timeline”.
                </span>
              )}
              {audioSegments.map((seg) => {
                const asset = audioAssets.find(a => a.id === seg.assetId);
                const isSelected = seg.id === selectedAudioSegmentId;
                return (
                  <div
                    key={seg.id}
                    onClick={() => setSelectedAudioSegmentId(seg.id)}
                    style={{
                      minWidth: "160px",
                      padding: "4px",
                      borderRadius: "4px",
                      border: isSelected ? "2px solid #16a34a" : "1px solid #ccc",
                      background: isSelected ? "#ecfdf3" : "#f9fafb",
                      cursor: "pointer",
                      fontSize: "0.8rem",
                    }}
                  >
                    <div style={{ fontWeight: 600, marginBottom: "2px" }}>
                      {asset?.name ?? "Audio"}
                    </div>
                    <div>start: {seg.start.toFixed(2)}s</div>
                    <div>end: {seg.end.toFixed(2)}s</div>
                    <button
                      style={{ marginTop: "4px", fontSize: "0.7rem" }}
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteAudioSegment(seg.id);
                      }}
                    >
                      Eliminar
                    </button>
                  </div>
                );
              })}
            </div>

            {/* Controles del segmento de audio seleccionado */}
            {selectedAudioSegment && (
              <div style={{ marginTop: "8px", padding: "8px", borderRadius: "4px", border: "1px solid #ddd", fontSize: "0.8rem" }}>
                <div style={{ marginBottom: "4px", fontWeight: 600 }}>Editar segmento de audio</div>
                <div style={{ display: "flex", gap: "8px", alignItems: "center", marginBottom: "4px" }}>
                  <label>
                    start (s):{" "}
                    <input
                      type="number"
                      step="0.1"
                      value={selectedAudioSegment.start}
                      onChange={(e) =>
                        updateAudioSegment(selectedAudioSegment.id, { start: Number(e.target.value) || 0 })
                      }
                      style={{ width: "80px" }}
                    />
                  </label>
                  <label>
                    end (s):{" "}
                    <input
                      type="number"
                      step="0.1"
                      value={selectedAudioSegment.end}
                      onChange={(e) =>
                        updateAudioSegment(selectedAudioSegment.id, { end: Number(e.target.value) || 0 })
                      }
                      style={{ width: "80px" }}
                    />
                  </label>
                </div>
              </div>
            )}
          </div>
        </section>
      </main>
    </div>
  );
};

export default VideoEditor;
